import { Link, Navigate } from "react-router-dom";
import React, { useEffect,useState } from 'react';
import './css/Login.css';

import axios from "axios";
import FetchEnpoint from "./components/EndpointFinder";

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async () => {

    try {
      const response = await axios.post('/login', {
        username: username,
        password: password,
      });

      // Se la richiesta ha successo, gestisci la risposta qui
      console.log('Risposta dal server:', response.data);
      
      // Esempio: se il login ha avuto successo, esegui un'azione (come salvare il token di accesso)
      // localStorage.setItem('token', response.data.token);
    } catch (error) {
      // Se la richiesta non ha avuto successo, gestisci l'errore qui
      console.error('Errore durante il login:', error.response.data.error);
      setErrorMessage('Credenziali non valide. Riprova.');
    }
  };

  useEffect(() => {
    handleSubmit();
  }, []);


  return (
    <div className="title">
    <div style={{margin:'auto'}}>
      <h2>Login</h2>
      <form className='loginform' onSubmit={handleSubmit}>
        <input type="text" name="username" placeholder="Username" value={username} onChange={handleUsernameChange} required />
        <input type="password" name="password" placeholder="Password" value={password} onChange={handlePasswordChange} required />
        <input type="submit" value="Login" style={{ backgroundColor: '#007bff', color: '#fff', cursor: 'pointer' }} />
      </form>
    </div>
    </div>
  )
}
export default Login;
